import UIKit

public protocol TooltipMarkerViewModel: FibCoreViewModel {
	var backgroundColor: UIColor {get set}
	var orientation: TriangleView.ViewModel.Orientation {get set}
	init(backgroundColor: UIColor, orientation: TriangleView.ViewModel.Orientation)
}

public class TriangleView: FibCoreView {
    
    // MARK: - Views
    let imageView = UIImageView()
    
    // MARK: - Variables
    
    // MARK: - UI Configuration
	public override func configureUI() {
        super.configureUI()
        contentView.addSubview(imageView)
    }
    
    var vm: ViewModel?
    
	public override func layoutSubviews() {
        super.layoutSubviews()
        let rect = bounds
        UIGraphicsBeginImageContextWithOptions(rect.size, false, UIScreen.main.scale)
        
        guard let context = UIGraphicsGetCurrentContext(),
              let data = self.data as? ViewModel else { return }
        switch data.orientation {
        case .up:
            context.move(to: .init(x: rect.minX, y: rect.maxY))
            context.addLine(to: .init(x: rect.maxX, y: rect.maxY))
            context.addLine(to: .init(x: rect.midX, y: rect.minY))
            context.closePath()
        case .down:
            context.move(to: .init(x: rect.minX, y: rect.minY))
            context.addLine(to: .init(x: rect.maxX, y: rect.minY))
            context.addLine(to: .init(x: rect.midX, y: rect.maxY))
            context.closePath()
        }
		context.setFillColor(data.backgroundColor.cgColor)
        context.fillPath()
        let image = UIGraphicsGetImageFromCurrentImageContext()
        UIGraphicsEndImageContext()
        imageView.image = image
        imageView.frame = bounds
    }
    
	public override func sizeWith(_ targetSize: CGSize, data: ViewModelWithViewClass?, horizontal: UILayoutPriority, vertical: UILayoutPriority) -> CGSize? {
		return CGSize(width: 10, height: 5)
    }
    
    // MARK: - ViewModel
    
    public class ViewModel: FibCoreViewModel, TooltipMarkerViewModel {
        
		public var backgroundColor: UIColor = TooltipLabel.defaultTooltipAppearance.backgroundColor
		public var orientation: Orientation = .down
        
        public enum Orientation {
            case up
            case down
        }
        
		required public init(
			backgroundColor: UIColor = TooltipLabel.defaultTooltipAppearance.backgroundColor,
			orientation: Orientation = .down
		) {
            self.backgroundColor = backgroundColor
            self.orientation = orientation
        }
        
		public override func viewClass() -> ViewModelConfigurable.Type {
            TriangleView.self
        }
    }
	public override func configure(with data: ViewModelWithViewClass?) {
        super.configure(with: data)
        self.vm = data as? ViewModel
    }
}
